import React, {useEffect} from "react";
import {useState} from "react";
import Styles from "./Chat.module.css";
import {connect, sendMsg} from "../../api";

import gent from "../Smile.gent.gif"

export const Chat = () => {
    const [messages, setMessgae] = useState<string[]>([]);
    const [value, setValue] = useState<string>("");
    const [nick, setNickname] = useState<string>("USER");
    const send = () => {
        if (value === "" || nick === "") return
        setValue("")
        sendMsg(`${nick + ":"} ${value}`);
    };

    connect((msg: any) => {
        setMessgae([...messages, JSON.parse(msg.data)]);
    });


    useEffect(() => {

        setInterval(() => {
            window.location.reload()
        }, 200000)
    }, [])

    return (
        <div className={Styles.chatBlock}>
            <img src={gent} alt="tests" />
            <ul>
                {messages.map((msg: any, i: number) => (
                    <li key={i}>{msg.Body}</li>
                ))}
            </ul>
            <div className={Styles.sendMessage}>
                <input
                    onChange={(e) => setNickname(e.target.value)}
                    value={nick}
                    type="text" className={Styles.nicknameValue}/>
                <textarea
                    value={value}
                    className={Styles.inputMessage}
                    onChange={(e) => setValue(e.target.value)}
                >{value as string}</textarea>
                <button
                    className={Styles.btnSend}
                    onClick={() => send()}>SEND
                </button>
            </div>
        </div>
    );
};
