import React, { useEffect } from "react";
import { useState } from "react";
import Styles from "./Chat.module.css";
import { connect, sendMsg } from "../../api";


export const Chat = () => {
    const [messages, setMessgae] = useState<string[]>([]);
    const [value, setValue] = useState<string>("");
    const [nick, setNickname] = useState<string>("USER");
    const [smile, setSmile] = useState<boolean>(false);


    const [smiles] = useState([
        {
            name: "yes",
            width: '20',
            height: '20',

        },
        {
            name: "ahtung",
            width: "30",
            height: "20",
        },
        {
            name: "gent",
            width: '20',
            height: '20',
        },
        {
            name: "conf",
            width: '20',
            height: '20'
        },
        {
            name: "doctor",
            width: "40",
            height: "35",
        },
        {
            name: "privet",
            width: '35',
            height: '35',
        },
        {
            name: "hello",
            width: '35',
            height: '35',
        },
        {
            name: "congr",
            width: "55",
            height: "40"
        },
        {
            name: "newyear",
            width: "55",
            height: "40",
        },
        {
            name: "old",
            width: "40",
            height: "30",
        },
        {
            name: "wall",
            width: '35',
            height: "30"
        },
        {
            name: "friday",
            width: '75',
            height: "35"
        }
    ])


    const send = () => {
        if (value === "" || nick === "") return
        setValue("")
        sendMsg(`${nick + ":"} ${value}`);
    };

    connect((msg: any) => {
        setMessgae([...messages, JSON.parse(msg.data)]);
    });

    useEffect(() => {
        setInterval(() => {
            window.location.reload()
        }, 200000)
    }, [])

    function addSmile(smile: string) {
        const text = document.querySelector("#text")
        text.innerHTML = smile
    }

    return (
        <div className={Styles.chatBlock}>
            <img width="20" src={require('../Smile/gent.gif')} alt="tests" />
            <ul>
                {messages.map((msg: any, i: number) => (
                    <li key={i}>{msg.Body}</li>
                ))}
            </ul>
            <div className={Styles.inputBox}>
                <div className={Styles.sendMessage}>
                    <input
                        onChange={(e) => setNickname(e.target.value)}
                        value={nick}
                        type="text" className={Styles.nicknameValue} />
                    <textarea
                        id="text"
                        value={value}
                        className={Styles.inputMessage}
                        onChange={(e) => setValue(e.target.value)}
                    >{value as string}</textarea>
                    <div style={{display: "flex"}}>
                        <button
                            className={Styles.btnSend}
                            onClick={() => send()}>SEND
                        </button>
                        <button className={Styles.btnSmile}
                            onClick={() => setSmile(!smile)}
                        >SMILE</button>
                    </div>
                </div>
                <div className={Styles.smileBox}>
                    {smile && <div className={Styles.smileView}>
                            {smiles.map((obj) => <img width={obj.width} height={obj.height} src={require(`../Smile/${obj.name}.gif`)} 
                                                        className={Styles.imgSmile}
                                                        onClick={() => addSmile(`:${obj.name}:`)}
                                                        />)}
                        </div>}
                </div>

            </div>
        </div>
    );
};
